import React, { useRef } from 'react';
import YouTube from 'react-youtube';

interface YoutubeWrapperProps {
    setYtPlayer: (player: any) => void;
}

function YoutubeWrapper({ setYtPlayer }: YoutubeWrapperProps) {
    const playerRef = useRef<any>(null);

    window.addEventListener('keypress', (e) => {
        if (!playerRef) {
            return;
        }

        switch (e.key) {
            case 'ArrowRight':
                playerRef.current.currentTime += 5;
                break;
            case 'ArrowLeft':
                playerRef.current.currentTime -= 5;
                break;
            default:
                // Ignore other keys
                break;
        }

    })


    const onPlayerReady = (event: any) => {
        playerRef.current = event.target;
        setYtPlayer(playerRef);
    }

    const handlePlay = () => {
        if (playerRef.current) {
            playerRef.current.playVideo();
        }
    };

    const handlePause = () => {
        if (playerRef.current) {
            playerRef.current.pauseVideo();
        }
    };

    const handleRewind = () => {
        if (playerRef.current) {
            const currentTime = playerRef.current.getCurrentTime();
            playerRef.current.seekTo(currentTime - 5, true);
        }
    };

    const handleForward = () => {
        if (playerRef.current) {
            const currentTime = playerRef.current.getCurrentTime();
            playerRef.current.seekTo(currentTime + 5, true);
        }
    };

    const opts = {
        height: '390',
        width: '640',
        playerVars: {
            autoplay: 0,
        },
    };

    return (
        <div>
            <YouTube videoId="QVOUKUDD4GU" opts={opts} onReady={onPlayerReady} />
            <button onClick={handlePlay}>Play</button>
            <button onClick={handlePause}>Pause</button>
            <button onClick={handleRewind}>Rewind 5s</button>
            <button onClick={handleForward}>Forward 5s</button>
        </div>
    );
}

export default YoutubeWrapper;
