import { StatType } from "../types/enums";
import { StatLog } from "../types/types";

const getDefaultStats = () => {
    return {
        [StatType.twoPointsMade]: 0,
        [StatType.twoPointsMissed]: 0,
        [StatType.threePointsMade]: 0,
        [StatType.threePointsMissed]: 0,
        [StatType.oRebounds]: 0,
        [StatType.dRebounds]: 0,
        [StatType.assists]: 0,
        [StatType.steals]: 0,
        [StatType.blocks]: 0,
        [StatType.turnovers]: 0
    };
}

const createRandomStats = () => {
}

const playerOne = {
    id: 1,
    name: "Sang",
    number: "13",
    stats: {
        [StatType.twoPointsMade]: 5,
        [StatType.twoPointsMissed]: 3,
        [StatType.threePointsMade]: 2,
        [StatType.threePointsMissed]: 4,
        [StatType.oRebounds]: 5,
        [StatType.dRebounds]: 6,
        [StatType.assists]: 7,
        [StatType.steals]: 8,
        [StatType.blocks]: 9,
        [StatType.turnovers]: 0,
    }
};

const playerTwo = {
    id: 2,
    name: "Kenny",
    number: "0",
    stats: {
        [StatType.twoPointsMade]: 4,
        [StatType.twoPointsMissed]: 6,
        [StatType.threePointsMade]: 2,
        [StatType.threePointsMissed]: 4,
        [StatType.oRebounds]: 6,
        [StatType.dRebounds]: 5,
        [StatType.assists]: 4,
        [StatType.steals]: 3,
        [StatType.blocks]: 2,
        [StatType.turnovers]: 1,
    }
};
const playerThree = {
    id: 3,
    name: "Bryce",
    number: "11",
    stats: createRandomStats(),
};
const playerFour = {
    id: 4,
    name: "Jeff",
    number: "25",
    stats: createRandomStats(),
};
const playerFive = {
    id: 5,
    name: "Andrew",
    number: "7",
    stats: createRandomStats(),
};
const playerSix = {
    id: 6,
    name: "Rodell",
    number: "15",
    stats: createRandomStats(),
}
const playerSeven = {
    id: 7,
    name: "Mike",
    number: "23",
    stats: createRandomStats(),
};
const playerEight = {
    id: 8,
    name: "Max",
    number: "16",
    stats: createRandomStats(),
};
const playerNine = {
    id: 9,
    name: "Timmy",
    number: "1",
    stats: createRandomStats(),
};
const playerTen = {
    id: 10,
    name: "Kevin",
    number: "00",
    stats: createRandomStats(),
};


const randomStats = [];

// playerId, statType, time
for (let i = 1; i < 11; i++) {
    for (let type in StatType) {
        const rand = Math.round(Math.random() * 6);
        for (let j = 0; j < rand; j++) {
            let newStat: StatLog = {
                playerId: i,
                statType: type,
                time: (Math.round(Math.random() * 300))
            }

            randomStats.push(newStat);
        }
    }
}

console.log(randomStats);

const mockPlayers = [playerOne,
    playerTwo,
    playerThree,
    playerFour,
    playerFive,
    playerSix,
    playerSeven,
    playerEight,
    playerNine,
    playerTen
]

export { mockPlayers };
