import React, { Ref, useRef } from 'react';
import YouTube from 'react-youtube';

interface YoutubeWrapperProps {
    setPlayerInstance: (player: YouTube) => void;
    playerInstance: YouTube | null;
};

function YoutubeWrapper({ setPlayerInstance, playerInstance }: YoutubeWrapperProps) {

    const onPlayerReady = (event: any) => {
        setPlayerInstance(event.target);
    }

    const handlePlay = () => {
        if (playerInstance) {
            playerInstance.playVideo();
        }
    };

    const handlePause = () => {
        if (playerInstance) {
            playerInstance.pauseVideo();
        }
    };

    const handleRewind = () => {
        if (playerInstance) {
            const currentTime = playerInstance.getCurrentTime();
            playerInstance.seekTo(currentTime - 5, true);
        }
    };

    const handleForward = () => {
        if (playerInstance) {
            const currentTime = playerInstance.getCurrentTime();
            playerInstance.seekTo(currentTime + 5, true);
        }
    };

    const opts = {
        height: '390',
        width: '640',
        playerVars: {
            autoplay: 0,
        },
    };

    return (
        <div>
            <YouTube videoId="QVOUKUDD4GU" opts={opts} onReady={onPlayerReady} />
            <button onClick={handlePlay}>Play</button>
            <button onClick={handlePause}>Pause</button>
            <button onClick={handleRewind}>Rewind 5s</button>
            <button onClick={handleForward}>Forward 5s</button>
        </div>
    );
}

export default YoutubeWrapper;
